function CollapsibleTrigger({
  children,
  className,
  asChild = false,
  ...props
}: React.ComponentProps<typeof BaseCollapsible.Trigger> & { asChild?: boolean }) {
  const triggerProps = {
    'data-slot': 'alert-dialog-trigger' as const,
    className,
    ...props,
