'use client';

import { Alert, AlertIcon, AlertTitle } from '@/components/ui/alert';
import {
  Autocomplete,
  AutocompleteContent,
  AutocompleteEmpty,
  AutocompleteInput,
  AutocompleteItem,
  AutocompleteList,
} from '@/components/ui/base-autocomplete';
import { Button } from '@/components/ui/base-button';
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '@/components/ui/base-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { RiCheckboxCircleFill } from '@remixicon/react';
import { useForm } from 'react-hook-form';
import { toast } from 'sonner';
import { z } from 'zod';

export default function AutocompleteForm() {
  const FormSchema = z.object({
    tag: z.string().min(1, 'Please select a tag.'),
  });

  const form = useForm<z.infer<typeof FormSchema>>({
    resolver: zodResolver(FormSchema),
    defaultValues: { tag: '' },
    mode: 'onSubmit',
  });

  function onSubmit(data: z.infer<typeof FormSchema>) {
    toast.custom((t) => (
      <Alert variant="mono" icon="primary" onClose={() => toast.dismiss(t)}>
        <AlertIcon>
          <RiCheckboxCircleFill />
        </AlertIcon>
        <AlertTitle>Your form has successfully submitted</AlertTitle>
      </Alert>
    ));

    form.reset();
  }

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(onSubmit)} className="w-[300px] space-y-6">
        <FormField
          control={form.control}
          name="tag"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Tag</FormLabel>
              <FormControl>
                <Autocomplete
                  items={tags}
                  value={field.value}
                  onValueChange={field.onChange}
                  itemToStringValue={(item: unknown) => (item as Tag).value}
                >
                  <AutocompleteInput placeholder="e.g. feature" aria-invalid={!!form.formState.errors.tag} {...field} />
                  <AutocompleteContent>
                    <AutocompleteEmpty>No tags found.</AutocompleteEmpty>
                    <AutocompleteList>
                      {(tag) => (
                        <AutocompleteItem key={tag.id} value={tag}>
                          {tag.value}
                        </AutocompleteItem>
                      )}
                    </AutocompleteList>
                  </AutocompleteContent>
                </Autocomplete>
              </FormControl>
              <FormDescription>Select a tag for your project</FormDescription>
              <FormMessage />
            </FormItem>
          )}
        />
        <div className="flex items-center justify-end gap-2.5">
          <Button type="submit">Submit</Button>
        </div>
      </form>
    </Form>
