import { useRef, useState } from 'react';
import { Button } from '@/components/ui/base-button';
import { Input, InputWrapper } from '@/components/ui/base-input';
import { X } from 'lucide-react';

export default function InputDemo() {
  const [inputValue, setInputValue] = useState('Click to clear');
  const inputRef = useRef<HTMLInputElement>(null);

  const handleClearInput = () => {
    setInputValue('');
    if (inputRef.current) {
      inputRef.current.focus();
    }
  };
  return (
    <div className="w-80">
      <InputWrapper>
        <Input
          placeholder="Type some input"
          ref={inputRef}
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <Button onClick={handleClearInput} variant="dim" className="-me-4" disabled={inputValue === ''}>
          {inputValue !== '' && <X size={16} />}
        </Button>
      </InputWrapper>
    </div>
  );
}

base-input/copy-to-clipboard.code:
import { useRef } from 'react';
import { useCopyToClipboard } from '@/hooks/use-copy-to-clipboard';
import { Button } from '@/components/ui/base-button';
import { Input, InputWrapper } from '@/components/ui/base-input';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { CheckIcon, CopyIcon } from 'lucide-react';

export default function InputDemo() {
  const { copy, copied } = useCopyToClipboard();
  const inputRef = useRef<HTMLInputElement>(null);

  const handleCopy = () => {
    if (inputRef.current) {
      copy(inputRef.current.value);
    }
  };

  return (
    <div className="w-80">
      <InputWrapper>
        <Input
          type="email"
          placeholder="Copy to clipboard"
          defaultValue="pnpm install @themesstrictweb/restrictui"
          ref={inputRef}
        />
        <TooltipProvider delayDuration={0}>
          <Tooltip>
            <TooltipTrigger asChild>
              <Button onClick={handleCopy} variant="dim" disabled={copied} className="-me-3.5">
                {copied ? <CheckIcon className="stroke-green-600" size={16} /> : <CopyIcon size={16} />}
              </Button>
            </TooltipTrigger>
            <TooltipContent className="px-2 py-1 text-xs">Copy to clipboard</TooltipContent>
          </Tooltip>
        </TooltipProvider>
      </InputWrapper>
    </div>
  );
}

