  Command,
  CommandCheck,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
  CommandSeparator,
} from '@/components/ui/command';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Plus } from 'lucide-react';

const topCities = [
  {
    value: 'amsterdam',
    label: 'Amsterdam, Netherlands',
  },
  {
    value: 'london',
    label: 'London, UK',
  },
  {
    value: 'paris',
    label: 'Paris, France',
  },
  {
    value: 'tokyo',
    label: 'Tokyo, Japan',
  },
  {
    value: 'san_francisco',
    label: 'San Francisco, USA',
  },
